css:
@page {
  size: 126mm 60mm; /* 2 labels per row */
  margin: 0;
}
html, body {
  margin: 0;
  padding: 0;
}
.wrapper {
  width: 126mm;
  height: 60mm;
  display: flex;
}
.label {
  width: 63mm;
  height: 60mm;
  padding: 3mm;
  font-family: Arial, Helvetica, sans-serif;
  font-size: 7px;
  box-sizing: border-box;
  overflow: hidden;
  border: 1px dashed #ccc; /* optional for testing layout */
  text-align: left!important;
}
.barcode {
  /*text-align: center;*/
  margin-top: 2mm;
}
.barcode img {
  width: 59mm;
  height: auto;
}

html:
{% set barcodes = frappe.get_all(
    "Gate Entry Box Barcode",
    filters={"incoming_logistics_no": doc.name},
    fields=["name","box_barcode","total_barcode"]
) %}

{% for i in range(0, barcodes|length, 2) %}
<div class="wrapper">

  <!-- LEFT LABEL -->
  {% set left = barcodes[i] %}
  <div class="label">
    <div style="text-align:center;font-weight:700;font-size:10px;">
      {{ doc.consignor or "None" }}
    </div><br/>

    <div style="font-size:8px; line-height:1.2; font-weight:600;">
    PO No: {{ doc.purchase_no or "-" }}<br>
    <div style="display: flex; justify-content: space-between;">
        <span>LR No: {{ doc.lr_document_no or "-" }}</span>
        <span>Date: {{ doc.date or "" }}</span>
    </div>
</div>


    <div class="barcode">
      <img src="https://barcode.tec-it.com/barcode.ashx?data={{ left.box_barcode }}&code=Code128&dpi=300">
    </div>
<div style="position: relative; height: 30px;margin-top:15px;"> <!-- parent container -->
    <span style="
        position: absolute;
        right: 0;        /* right side */
        bottom: 0;       /* bottom corner */
        background-color: black;
        color: white;
        padding: 8px 16px;
        border-radius: 60px;
        font-weight: bold;
        font-size: 25px;
        ">
        {{ left.total_barcode }}
    </span>
</div>

  </div>

  <!-- RIGHT LABEL -->
  {% if i + 1 < barcodes|length %}
    {% set right = barcodes[i+1] %}
    <div class="label">
      <div style="text-align:center;font-weight:700;font-size:10px;">
        {{ doc.consignor or "None" }}
      </div><br/>

     <div style="font-size:8px; line-height:1.2; font-weight:600;">
    PO No: {{ doc.purchase_no or "-" }}<br>
    <div style="display: flex; justify-content: space-between;">
        <span>LR No: {{ doc.lr_document_no or "-" }}</span>
        <span>Date: {{ doc.date or "" }}</span>
    </div>
</div>

      <div class="barcode">
        <img src="https://barcode.tec-it.com/barcode.ashx?data={{ right.box_barcode }}&code=Code128&dpi=300">
      </div>

     <div style="position: relative; height: 30px;margin-top:15px;"> <!-- parent container -->
    <span style="
        position: absolute;
        right: 0;        /* right side */
        bottom: 0;       /* bottom corner */
        background-color: black;
        color: white;
        padding: 8px 16px;
        border-radius: 60px;
        font-weight: bold;
        font-size: 25px;
        ">
        {{ right.total_barcode }}
    </span>
</div>


    </div>
  {% endif %}

</div>

<div style="page-break-after: always;"></div>
{% endfor %}
