css:
@page {
  size: 126mm 50mm; /* 2 labels per row */
  margin: 0;
}
html, body {
  margin: 0;
  padding: 0;
}
.wrapper {
  width: 126mm;
  height: 50mm;
  display: flex;
}
.label {
  width: 63mm;
  height: 50mm;
  padding: 3mm;
  font-family: Arial, Helvetica, sans-serif;
  font-size: 7px;
  box-sizing: border-box;
  overflow: hidden;
  border: 1px dashed #ccc; /* optional for testing layout */
  text-align: left!important;
}
.barcode {
  /*text-align: center;*/
  margin-top: 2mm;
}
.barcode img {
  width: 56mm;
  height: auto;
}
