css:
@page {
    size: 126mm 50mm;
    margin: 0;
}

html, body {
    margin: 0;
    padding: 0;
    background: #fff;
}

.wrapper {
    width: 126mm;
    height: 50mm;
    display: flex;
    page-break-inside: avoid;
    page-break-after: always;
}

.label {
    width: 63mm;
    height: 50mm;
    padding: 3mm;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 7px;
    box-sizing: border-box;
    overflow: hidden;
    border: 1px dashed #ccc;
    text-align: left !important;
    display: flex;
    flex-direction: column;
}

.brand-header {
    text-align: center;
    font-weight: 700;
    font-size: 8px;
    text-transform: uppercase;
}

.item-details {
    font-size: 5.5px;
    line-height: 1.2;
    font-weight: 600;
    margin-top: 1mm;
}

.barcode-area {
    text-align: center;
    margin-top: auto;
}

.barcode-area img {
    width: 56mm;
    height: 20mm; /* Barcode ki lambi dandi (bars) ke liye */
    display: block;
    margin: 0 auto;
}

.serial-number {
    font-size: 16px; /* Bada font size barcode ke niche */
    font-weight: bold;
    margin-top: -1mm;
    text-align: center;
    letter-spacing: 1px;
}

.mrp-section {
    text-align: center;
    font-weight: 700;
    font-size: 8px;
    margin-top: 1mm;
}
