css:
.items-table th, .items-table td { padding:5px; }
.header-logo {
    width: 200px !important;
    vertical-align: top !important;
}

html:
{% set cgst_rate = [] %}
{% set sgst_rate = [] %}
{% set igst_rate = [] %}
{% set cgst_account_head = [] %}
{% set sgst_account_head = [] %}
{% set igst_account_head = [] %}
{% set tax_accounts = [] %}

{%- for tax in doc.taxes %}
  {% if 'CGST' in tax.account_head %}
    {% set _ = cgst_account_head.append(tax.account_head) %}
    {% set _ = cgst_rate.append(tax.rate) %}
    {% set _ = tax_accounts.append(tax.description) %}
  {% endif %}
  {% if 'SGST' in tax.account_head %}
    {% set _ = sgst_account_head.append(tax.account_head) %}
    {% set _ = sgst_rate.append(tax.rate) %}
    {% set _ = tax_accounts.append(tax.description) %}
  {% endif %}
  {% if 'IGST' in tax.account_head %}
    {% set _ = igst_account_head.append(tax.account_head) %}
    {% set _ = igst_rate.append(tax.rate) %}
    {% set _ = tax_accounts.append(tax.description) %}
  {% endif %}
{%- endfor %}

{# Fetch Company Address via Dynamic Link #}
{% set company_addr_name = frappe.db.get_value('Dynamic Link', {'link_doctype':'Company','link_name':doc.company}, 'parent') %}
{% set company_addr = frappe.get_doc('Address', company_addr_name) if company_addr_name else None %}

<section class="content">
  <!-- Header: Logo Left, Company Info Right -->
  <table style="width:100%; margin-bottom:20px;">
    <tr>
      <td style="width:50%;">
        <img src="{{ frappe.db.get_value('Company', doc.company, 'logo_for_printing') }}" class="header-logo">
      </td>
      <td style="width:50%; vertical-align:top;">
        <p><b>{{ doc.company }}</b></p>
          <p>GSTIN: 07AAACO9403G1ZL</p>
          <p>PAN: AAACO9403G</p>
          <p>CIN: U51109DL2007PTC158963</p>
          <p>Address: Chandni Chowk, New Delhi, India</p>
          <p>Phone: 91-9311195501</p>
          <p>Email: accounts@tzu.com</p>
      </td>
    </tr>
  </table>

  <!-- Customer & Invoice Info -->
  <table style="width:100%; margin-bottom:20px;">
    <tr>
      <td style="width:50%; vertical-align:top;">
        <b>Customer:</b> {{ doc.customer_name or '' }}<br>
        <b>Bill To:</b><br>
        {% if doc.customer_address %}
          {% set addr = frappe.db.get_value('Address', doc.customer_address, ['address_line1','address_line2','city','state','pincode','country'], as_dict=True) %}
          {% if addr %}
            {{ addr.address_line1 }}<br>
            {{ addr.address_line2 }}<br>
            {{ addr.city }}, {{ addr.state }} - {{ addr.pincode }}<br>
            {{ addr.country }}
          {% endif %}
        {% endif %}
      </td>
      <td style="width:50%; vertical-align:top;">
        <b>Invoice No:</b> {{ doc.name }}<br>
        <b>Invoice Date:</b> {{ doc.posting_date }}<br>
        <b>Due Date:</b> {{ doc.due_date }}
      </td>
    </tr>
  </table>

  <!-- Items Table -->
  <table style="width:100%; border-collapse: collapse; margin-bottom:15px;">
    <thead>
      <tr style="background:#f2f2f2;">
        <th style="border:1px solid #ccc; text-align:center;">#</th>
        <th style="border:1px solid #ccc;">Item</th>
        <th style="border:1px solid #ccc;">Item Code</th>
        <th style="border:1px solid #ccc; text-align:right;">Qty</th>
        <th style="border:1px solid #ccc; text-align:right;">Rate</th>
        <th style="border:1px solid #ccc; text-align:right;">Amount</th>
      </tr>
    </thead>
    <tbody>
      {% for item in doc.items %}
      <tr>
        <td style="border:1px solid #ccc; text-align:center;">{{ loop.index }}</td>
        <td style="border:1px solid #ccc;">{{ item.item_name }}</td>
        <td style="border:1px solid #ccc;">{{ item.item_code }}</td>
        <td style="border:1px solid #ccc; text-align:right;">{{ item.qty }} {{ item.uom }}</td>
        <td style="border:1px solid #ccc; text-align:right;">{{ item.get_formatted('net_rate', doc) }}</td>
        <td style="border:1px solid #ccc; text-align:right;">{{ item.get_formatted('net_amount', doc) }}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>

  <!-- Totals -->
  <table style="width:40%; float:right; border-collapse: collapse;">
    <tr>
      <td style="text-align:right;">Sub Total:</td>
      <td style="text-align:right;">{{ doc.get_formatted('total', doc) }}</td>
    </tr>
    {% if doc.discount_amount %}
    <tr>
      <td style="text-align:right;">Discount:</td>
      <td style="text-align:right;">{{ doc.get_formatted('discount_amount', doc) }}</td>
    </tr>
    {% endif %}
    {% for tax in doc.taxes %}
      {% if tax.tax_amount %}
      <tr>
        <td style="text-align:right;">{{ tax.description }} ({{ tax.rate }}%)</td>
        <td style="text-align:right;">{{ tax.get_formatted('tax_amount') }}</td>
      </tr>
      {% endif %}
    {% endfor %}
    <tr>
      <td style="text-align:right;"><b>Grand Total:</b></td>
      <td style="text-align:right;"><b>{{ doc.get_formatted('grand_total', doc) }}</b></td>
    </tr>
  </table>
  <div style="clear:both;"></div>

  <!-- In Words -->
  {% if doc.in_words %}
  <p><b>In Words:</b> {{ doc.in_words }}</p>
  {% endif %}

  <!-- Terms & Conditions -->
  {% if doc.terms %}
  <hr>
  <p><b>Terms & Conditions:</b></p>
  {{ doc.terms }}
  {% endif %}
</section>
