html:
{% set it = frappe.get_doc("Item", doc.item_code) %}


<style>
@page {
  size: 238px 250px;
  margin: 0;
}
html, body {
  margin: 0;
  padding: 0;
}
* { box-sizing: border-box; }
</style>

<div style="
  width:238px;
  height:250px;
  padding:6px;
  overflow:hidden;
  font-family:'Arial Narrow','Helvetica Neue',Arial,sans-serif;
  color:#000;
">

  <!-- BRAND -->
  <div style="text-align:center;font-weight:700;font-size:15px;">
    {{ it.brand or "NONE" }}
  </div>

  <!-- DETAILS -->
  <div style="font-size:10px;line-height:1.15;font-weight:600;">
    Product - {{ it.item_name }}<br>
    Item Code - {{ it.item_code }}<br>
    Design No - {{ doc.name }}<br>
    Color - {{ it.custom_colour_name or "" }}<br>
    Size - {{ it.custom_size or "" }}<br>
    Fabric Top - {{ it.custom_top_fabrics or "" }}<br>
    Fabric Bottom - {{ it.custom_bottom_fabric or "" }}<br>
    Fabric Dupatta - {{ it.custom_dupatta_fabric or "" }}<br>
    Country of Origin - {{ it.country_of_origin or "INDIA" }}
  </div>

  <hr style="margin:3px 0;border-top:1px solid #000;">

  <!-- Barcode -->
  <div style="text-align:center;">
    <img src="https://barcode.tec-it.com/barcode.ashx?data={{ doc.item_code }}&code=Code128&translate-esc=true"
         style="width:55mm; height:auto;">
    <p style="margin:2px 0;"><b>{{ doc.item_code }}</b></p>
  </div>
  <!-- Optional MRP -->
  {% set mrp = frappe.db.get_value("Item Price", {"item_code": doc.item_code}, "price_list_rate") or 0 %}

    <div style="text-align:center; font-weight:bold; font-size:13px; margin-top:2px;">
      MRP ₹ {{ "%.2f"|format(mrp) }} <br>
      <small>(Inclusive of all taxes)</small>
    </div>


</div>
