html:
{% for item in doc.items %}
  {% if item.serial_and_batch_bundle %}

    {% set bundle = frappe.get_doc(
        "Serial and Batch Bundle",
        item.serial_and_batch_bundle
    ) %}

    {% set it = frappe.get_doc("Item", item.item_code) %}
    {% set mrp = frappe.db.get_value(
        "Item Price",
        {"item_code": item.item_code},
        "price_list_rate"
    ) or 0 %}

    {% for row in bundle.entries %}

<style>
@page {
  size: 238px 189px;
  margin: 0;
}
html, body {
  margin: 0;
  padding: 0;
}
* { box-sizing: border-box; }
</style>

<div style="
  width:238px;
  height:189px;
  padding:6px;
  overflow:hidden;
  font-family:'Arial Narrow','Helvetica Neue',Arial,sans-serif;
  color:#000;
">

  <!-- BRAND -->
  <div style="text-align:center;font-weight:700;font-size:8px;">
    {{ it.brand or "NONE" }}
  </div>

  <!-- DETAILS -->
  <div style="font-size:5px;line-height:1.15;font-weight:600;">
    Product - {{ it.item_name }}<br>
    Item No - {{ it.item_code }}<br>
    Design No - {{ it.item_code }}<br>
    Color - {{ it.custom_colour_name or "" }}<br>
    Size - {{ it.custom_size or "" }}<br>
    Fabric Top - {{ it.custom_top_fabrics or "" }}<br>
    Fabric Bottom - {{ it.custom_bottom_fabric or "" }}<br>
    Fabric Dupatta - {{ it.custom_dupatta_fabric or "" }}<br>
    Country of Origin - {{ it.country_of_origin or "INDIA" }}
  </div>

  <!-- BARCODE (SERIAL NUMBER) -->
  <div style="text-align:center;">
    <img src="https://barcode.tec-it.com/barcode.ashx?data={{ row.serial_no }}&code=Code128"
         style="width:55mm; height:auto;">
  </div>

  <!-- MRP -->
  <div style="text-align:center; font-weight:bold; font-size:10px; margin-top:1px;">
    MRP ₹ {{ "%.2f"|format(mrp) }} <br>
    <small>(Inclusive of all taxes)</small>
  </div>

</div>

<div style="page-break-after:always;"></div>

    {% endfor %}
  {% endif %}
{% endfor %}
