css:
@page {
  size: 126mm 50mm; /* 2 labels per row */
  margin: 0;
}
html, body {
  margin: 0;
  padding: 0;
}
.wrapper {
  width: 126mm;
  height: 50mm;
  display: flex;
}
.label {
  width: 63mm;
  height: 50mm;
  padding: 3mm;
  font-family: Arial, Helvetica, sans-serif;
  font-size: 7px;
  box-sizing: border-box;
  overflow: hidden;
  border: 1px dashed #ccc; /* optional for testing layout */
  text-align: left!important;
}
.barcode {
  /*text-align: center;*/
  margin-top: 2mm;
}
.barcode img {
  width: 56mm;
  height: auto;
}

html:
{% for item in doc.items %}
  {% if item.serial_and_batch_bundle %}
    {% set bundle = frappe.get_doc("Serial and Batch Bundle", item.serial_and_batch_bundle) %}
    {% set it = frappe.get_doc("Item", item.item_code) %}
    {% set mrp = frappe.db.get_value("Item Price", {"item_code": item.item_code}, "price_list_rate") or 0 %}

    {% for i in range(0, bundle.entries|length, 2) %}
      <div class="wrapper">
        <!-- LEFT LABEL -->
        {% set row1 = bundle.entries[i] %}
        <div class="label">
          <div style="text-align:center;font-weight:700;font-size:8px;">
            {{ it.brand or "NONE" }}
          </div>
          <div style="font-size:5px;line-height:1.15;font-weight:600;">
            Product: {{ it.item_name }}<br>
            Item No: {{ it.item_code }}<br>
            Design No: {{ it.item_code }}<br>
            Color: {{ it.custom_colour_name or "" }}<br>
            Size: {{ it.custom_size or "" }}<br>
            Fabric Top: {{ it.custom_top_fabrics or "" }}<br>
            Fabric Bottom: {{ it.custom_bottom_fabric or "" }}<br>
            Fabric Dupatta: {{ it.custom_dupatta_fabric or "" }}<br>
            Country: {{ it.country_of_origin or "INDIA" }}
          </div>
          <div class="barcode">
            <img src="https://barcode.tec-it.com/barcode.ashx?data={{ row1.serial_no }}&code=Code128&dpi=300">
          </div>
        </div>

        <!-- RIGHT LABEL -->
        {% if i + 1 < bundle.entries|length %}
          {% set row2 = bundle.entries[i+1] %}
          <div class="label">
            <div style="text-align:center;font-weight:700;font-size:8px;">
              {{ it.brand or "NONE" }}
            </div>
            <div style="font-size:5px;line-height:1.15;font-weight:600;">
              Product: {{ it.item_name }}<br>
              Item No: {{ it.item_code }}<br>
              Design No: {{ it.item_code }}<br>
              Color: {{ it.custom_colour_name or "" }}<br>
              Size: {{ it.custom_size or "" }}<br>
              Fabric Top: {{ it.custom_top_fabrics or "" }}<br>
              Fabric Bottom: {{ it.custom_bottom_fabric or "" }}<br>
              Fabric Dupatta: {{ it.custom_dupatta_fabric or "" }}<br>
              Country: {{ it.country_of_origin or "INDIA" }}
            </div>
            <div class="barcode">
              <img src="https://barcode.tec-it.com/barcode.ashx?data={{ row2.serial_no }}&code=Code128&dpi=300">
            </div>
          </div>
        {% endif %}
      </div>

      <div style="page-break-after:always;"></div>
    {% endfor %}
  {% endif %}
{% endfor %}
